import React from 'react';
import { useEffect } from 'react/cjs/react.production.min';
import "./GalleryImage.css";


function GalleryImage({id, imgSrc, credit, label}) {

    useEffect(() => {
        var modal = document.getElementById(`${id}`);
    var img = document.getElementById(`modal_${id}`);
    console.log(img);
    var modalImg = document.getElementById(`modalImg_${id}`)
    var captionText = document.getElementById(`caption_${id}`);
    var spanEl= document.getElementById(`close_${id}`)
    }, [])

    const onImgClick = () => {
        console.log(imgSrc)
        modal.style.display = "block";
        modalImg.src = imgSrc;
        captionText.innerHTML = credit;
    }

    const onModalClose = () => {
        modal.style.display = "none";
    }

    return (
        <>
            <img id={id} src={imgSrc} onClick={onImgClick} alt=""></img>
            <div id={`modal_${id}`} className="modal">
                <span id={`close_${id}`} className="close__button" onClick={onModalClose} >&times;</span>
                <img className="modal-content" id={`modalImg_${id}`}></img>
                <div className="caption" id={`caption_${id}`}></div>
            </div>
        </>
    )
}

export default GalleryImage;
