import React, {useState, useEffect} from 'react';

function Nav(){
    const [show, handleShow] = useState(false);

    useEffect(() => {
        window.addEventListener("scroll", () => {
            if (window.scrollY > 100){
                handleShow(true);
            } else handleShow(false);
        });
        return () => {
            window.removeEventListener("scroll", null);
        };
    }, []);

    return (
        <nav className="navbar navbar-expand-md tm-navbar" id="tmNav">
            <div className="container">
                <div className="tm-next">
                    <a href="#hero" className="navbar-brand">FLC Lab</a>
                </div>
                <button 
                    className="navbar-toggle"
                    type="button" 
                    data-toggle="collapse" 
                    data-target="#navbarSupportedContent" 
                    aria-controls="navbarSupportedContent"
                    aria-expanded="false"
                    aria-label="Toggle navigation"
                >
                    <i className="fas fa-bars navbar-toggler-icon"></i>
                </button>
                <div className="collapse navbar-collapse" id="navbarSupportedContent">
                    <ul className="navbar-nav ml-auto">
                        <li className="nav-item">
                            <a className="nav-link tm-nav-link" href="#introduction">Introduction</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link tm-nav-link" href="#members">Members</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link tm-nav-link" href="#gallery">Gallery</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link tm-nav-link" href="#publications">Publications</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link tm-nav-link" href="#funding">Funding</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link tm-nav-link" href="#contact">Contact Us</a>
                        </li>
                    </ul>
                </div>
            </div>
        </nav>
    )

}

export default Nav;
