import React, { useState } from 'react';
import "./Gallery.css";
import actinCamkii from "./img/gallery/actin-camkii.png";
import BassoonHomer from "./img/gallery/BassoonHomer.png"
import actinSpectrin from "./img/gallery/ActinSpectrin.png";
import confocalSted from "./img/gallery/confocal-sted-actin.png";
import cultures from "./img/gallery/cultures.png";
import Tubulin from "./img/gallery/LIVE610_Tubulin.png";
import synapticProteins from "./img/gallery/SynapticProteins.png";


function Gallery(){
    const [inFocus, setInFocus] = useState(false);
    const images = [
        {
            "id": 0,
            "img": actinCamkii,
            "label": "Actin and CamkII"
        },
        {
            "id": 1,
            "img": BassoonHomer,
            "label": "Bassoon Homer"
        }, 
        {
            "id": 2,
            "img": actinSpectrin,
            "label": "Actin Spectrin"
        },
        {
            "id": 3,
            "img": confocalSted,
            "label": "Confocal & STED"
        },
        {
            "id": 4,
            "img": cultures,
            "label": "Cultures"
        },
        {  
            "id": 5,
            "img": Tubulin,
            "label": "Tubulin"
        },
        {
            "id": 6,
            "img": synapticProteins,
            "label": "Synaptic Proteins",
        }
    ]

    const onmouseover = () => {    
        setInFocus(true);
    }

    const onmouseout = () => {
        setInFocus(false);
    }

    return (
        <div className="gallery__container">
            <div className="row__images">
                {images.map((image) => (

                    <div 
                        key={image.id}
                        className="image__container"
                        style={{ backgroundImage: `url('${image.img}')`, backgroundSize: "100% 100%", backgroundPositionX: "center" }}
                        onMouseOver={onmouseover}
                    >
                        <div className="image__label" hidden>{image.label}</div>
                    </div>

                ))}
            </div>
        </div>
    )
}

export default Gallery;
